mapper.select:
SELECT dh.*, u.user_name, c.title AS cookbook_title, c.cover AS cookbook_cover, n.id AS nutriment_id, n.name AS nutriment_name, cn.100g_value AS g100_value FROM diet_history dh LEFT JOIN user u ON dh.user_id = u.id LEFT JOIN cookbook c ON dh.cookbook_id = c.id LEFT JOIN cookbook_nutriment cn ON dh.cookbook_id = cn.cookbook_id LEFT JOIN nutriment n ON n.id = cn.nutriment_id
AND dh.id = #{id}
AND dh.user_id = #{userId}
AND dh.cookbook_id = #{cookbookId}
AND dh.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY dh.id DESC
LIMIT #{current},#{size}

SELECT COUNT(*)
FROM diet_history dh


        AND dh.id = #{id}


        AND dh.user_id = #{userId}


        AND dh.cookbook_id = #{cookbookId}


        AND dh.create_time BETWEEN #{startTime} AND #{endTime}
